mapper.select:
SELECT search_id, search_keyword, search_extend
FROM search_record
ORDER BY create_time DESC LIMIT #{size}
WHERE is_delete = 0

     AND u_id = #{uid}

mapper.update:
UPDATE browse_record
SET 'update_time' = #{br.updateTime}
WHERE 'browse_id' = #{bid} AND 'u_id' = #{uid} AND 'is_delete' = 0
